package findTheDifference;

import java.util.Arrays;

/**
 * @ClassName: FindTheDifference
 * @Author: jiaoxian
 * @Date: 2022/4/29 15:53
 * @Description: leetCode-389: 找不同
 */
public class FindTheDifference {

    public static char findTheDifference(String s, String t) {
        char[] charArray1 = s.toCharArray();
        Arrays.sort(charArray1);
        char[] charArray2 = t.toCharArray();
        Arrays.sort(charArray2);
        for (int i = 0; i < charArray1.length; i++) {
            if (charArray2[i] != charArray1[i]) {
                return charArray2[i];
            }
        }
        return charArray2[charArray2.length - 1];
    }

    public static char findTheDifference2(String s, String t) {
        return (char) (t.chars().sum() - s.chars().sum());
    }

    public static void main(String[] args) {
        String s = "";
        String t = "";
        System.out.println(findTheDifference2(s, t));
    }

}
